#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\privacy-sdk\node_modules\.pnpm\vitest@4.0.5_@types+node@24.9.2_tsx@4.20.6\node_modules\vitest\node_modules;D:\privacy-sdk\node_modules\.pnpm\vitest@4.0.5_@types+node@24.9.2_tsx@4.20.6\node_modules;D:\privacy-sdk\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/privacy-sdk/node_modules/.pnpm/vitest@4.0.5_@types+node@24.9.2_tsx@4.20.6/node_modules/vitest/node_modules:/mnt/d/privacy-sdk/node_modules/.pnpm/vitest@4.0.5_@types+node@24.9.2_tsx@4.20.6/node_modules:/mnt/d/privacy-sdk/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../vitest/vitest.mjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../vitest/vitest.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../vitest/vitest.mjs" $args
  } else {
    & "node$exe"  "$basedir/../vitest/vitest.mjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
